css:
.top-row {
    grid-row: 2 / span 1;
    grid-column: 2 / span 5;
    display: grid;
    grid-template-columns: repeat(5, 1fr);
}

.color-bar-area {
    grid-column: 5;
    grid-row: 1;

    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-rows: repeat(10, 1fr);
}

.bar-color-left {
    grid-column: 1;
    grid-row: 1;
    background-color: black; /* This stays black */
}

.bar-color-mid {
    grid-column: 2;
    grid-row: 1;
    background-color: var(--color3);
}

.bar-color-right {
    grid-column: 3;
    grid-row: 1;
    background-color: var(--color1);
}

.credit-space {
    grid-column: 1 / span 3;
    grid-row: 2 / span 9;

    margin-top: 0.8vmin;

    font-family: Helvetica;
    font-size: 0.735vmin;
}

.studio-logo {
    grid-column: 1;
    grid-row: 1;

    height: 5vmin;

    align-self: start;
    margin: 0;
}
